document.script:
function shop_AddRelatedItems() {
	var items = $('#result_list').children('div.list_item');
	var target = $('#related_items');

	items.each(function(index) {
		var checkbox = $(this).find('input');

		if (checkbox.is(':checked')) {
			var new_item = $(this).clone();
			var name = checkbox.attr('name');

			new_item
				.appendTo(target)
				.append(
					$('<input>',
					{
						type: 'hidden',
						name: name,
						value: 1
					})
				)
				.find('input[type=checkbox]').remove();

			checkbox.removeAttr('checked');
		}
	});

	Caracal.window_system.get_window('shop_search_results').close();
}

Caracal.window_system.get_window('shop_search_results').focus();
